@tailwind base;
@tailwind components;
@tailwind utilities;

/* LIGHT MODE - Colorful Theme */
:root {
  --button-outline: rgba(0,0,0, .10);
  --badge-outline: rgba(0,0,0, .05);

  /* Automatic computation of border around primary / danger buttons */
  --opaque-button-border-intensity: -8; /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(0,0,0, .03);
  --elevate-2: rgba(0,0,0, .08);

  --background: 270 60% 98%;

  --foreground: 270 20% 15%;

  --border: 280 40% 85%;

  --card: 250 70% 96%;

  --card-foreground: 270 20% 15%;

  --card-border: 280 50% 88%;

  --sidebar: 260 65% 94%;

  --sidebar-foreground: 270 20% 15%;

  --sidebar-border: 270 45% 82%;

  --sidebar-primary: 280 85% 55%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 200 70% 88%;

  --sidebar-accent-foreground: 270 20% 15%;

  --sidebar-ring: 280 85% 55%;

  --popover: 260 65% 95%;

  --popover-foreground: 270 20% 15%;

  --popover-border: 280 45% 84%;

  --primary: 280 85% 55%;

  --primary-foreground: 0 0% 100%;

  --secondary: 200 70% 85%;

  --secondary-foreground: 270 20% 15%;

  --muted: 260 40% 90%;

  --muted-foreground: 270 15% 40%;

  --accent: 340 80% 88%;

  --accent-foreground: 340 70% 25%;

  --destructive: 355 90% 55%;

  --destructive-foreground: 0 0% 100%;

  --input: 270 30% 85%;
  --ring: 280 85% 55%;
  --chart-1: 280 85% 55%;
  --chart-2: 170 75% 45%;
  --chart-3: 340 75% 58%;
  --chart-4: 45 95% 55%;
  --chart-5: 25 92% 58%;

  --font-sans: Inter, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: "SF Mono", Monaco, "Cascadia Code", Menlo, monospace;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 1px 1px 0px hsl(220 10% 10% / 0.02);
  --shadow-xs: 0px 1px 2px 0px hsl(220 10% 10% / 0.05);
  --shadow-sm: 0px 1px 2px 0px hsl(220 10% 10% / 0.06), 0px 1px 3px 0px hsl(220 10% 10% / 0.10);
  --shadow: 0px 2px 4px -1px hsl(220 10% 10% / 0.06), 0px 4px 6px -1px hsl(220 10% 10% / 0.10);
  --shadow-md: 0px 4px 6px -2px hsl(220 10% 10% / 0.05), 0px 10px 15px -3px hsl(220 10% 10% / 0.10);
  --shadow-lg: 0px 10px 15px -3px hsl(220 10% 10% / 0.08), 0px 20px 25px -5px hsl(220 10% 10% / 0.10);
  --shadow-xl: 0px 20px 25px -5px hsl(220 10% 10% / 0.08), 0px 25px 50px -12px hsl(220 10% 10% / 0.12);
  --shadow-2xl: 0px 25px 50px -12px hsl(220 10% 10% / 0.20);
  --tracking-normal: 0em;
  --spacing: 0.25rem;

  /* Automatically computed borders - intensity can be controlled by the user by the --opaque-button-border-intensity setting */

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

.dark {
  --button-outline: rgba(255,255,255, .10);
  --badge-outline: rgba(255,255,255, .05);

  --opaque-button-border-intensity: 9;  /* In terms of percentages */

  /* Backgrounds applied on top of other backgrounds when hovered/active */
  --elevate-1: rgba(255,255,255, .04);
  --elevate-2: rgba(255,255,255, .09);

  --background: 270 50% 12%;

  --foreground: 280 30% 92%;

  --border: 280 35% 25%;

  --card: 270 55% 15%;

  --card-foreground: 280 30% 92%;

  --card-border: 280 40% 22%;

  --sidebar: 265 60% 16%;

  --sidebar-foreground: 280 30% 92%;

  --sidebar-border: 275 45% 28%;

  --sidebar-primary: 280 85% 60%;

  --sidebar-primary-foreground: 0 0% 100%;

  --sidebar-accent: 200 60% 25%;

  --sidebar-accent-foreground: 200 80% 92%;

  --sidebar-ring: 280 85% 60%;

  --popover: 270 55% 18%;

  --popover-foreground: 280 30% 92%;

  --popover-border: 280 40% 28%;

  --primary: 280 85% 60%;

  --primary-foreground: 0 0% 100%;

  --secondary: 200 60% 30%;

  --secondary-foreground: 200 80% 92%;

  --muted: 270 40% 22%;

  --muted-foreground: 280 20% 70%;

  --accent: 340 70% 35%;

  --accent-foreground: 340 60% 92%;

  --destructive: 355 85% 55%;

  --destructive-foreground: 0 0% 100%;

  --input: 270 35% 28%;
  --ring: 280 85% 60%;
  --chart-1: 280 85% 65%;
  --chart-2: 170 75% 55%;
  --chart-3: 340 75% 65%;
  --chart-4: 45 95% 62%;
  --chart-5: 25 92% 65%;

  --shadow-2xs: 0px 1px 1px 0px hsl(220 0% 0% / 0.20);
  --shadow-xs: 0px 1px 2px 0px hsl(220 0% 0% / 0.30);
  --shadow-sm: 0px 1px 2px 0px hsl(220 0% 0% / 0.30), 0px 1px 3px 0px hsl(220 0% 0% / 0.40);
  --shadow: 0px 2px 4px -1px hsl(220 0% 0% / 0.30), 0px 4px 6px -1px hsl(220 0% 0% / 0.40);
  --shadow-md: 0px 4px 6px -2px hsl(220 0% 0% / 0.30), 0px 10px 15px -3px hsl(220 0% 0% / 0.40);
  --shadow-lg: 0px 10px 15px -3px hsl(220 0% 0% / 0.35), 0px 20px 25px -5px hsl(220 0% 0% / 0.40);
  --shadow-xl: 0px 20px 25px -5px hsl(220 0% 0% / 0.35), 0px 25px 50px -12px hsl(220 0% 0% / 0.50);
  --shadow-2xl: 0px 25px 50px -12px hsl(220 0% 0% / 0.60);

  /* Fallback for older browsers */
  --sidebar-primary-border: hsl(var(--sidebar-primary));
  --sidebar-primary-border: hsl(from hsl(var(--sidebar-primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --sidebar-accent-border: hsl(var(--sidebar-accent));
  --sidebar-accent-border: hsl(from hsl(var(--sidebar-accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --primary-border: hsl(var(--primary));
  --primary-border: hsl(from hsl(var(--primary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --secondary-border: hsl(var(--secondary));
  --secondary-border: hsl(from hsl(var(--secondary)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --muted-border: hsl(var(--muted));
  --muted-border: hsl(from hsl(var(--muted)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --accent-border: hsl(var(--accent));
  --accent-border: hsl(from hsl(var(--accent)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);

  /* Fallback for older browsers */
  --destructive-border: hsl(var(--destructive));
  --destructive-border: hsl(from hsl(var(--destructive)) h s calc(l + var(--opaque-button-border-intensity)) / alpha);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
  :root {
    --sidebar-background: 260 65% 94%;
    --sidebar-foreground: 270 20% 15%;
    --sidebar-primary: 280 85% 55%;
    --sidebar-primary-foreground: 0 0% 100%;
    --sidebar-accent: 200 70% 88%;
    --sidebar-accent-foreground: 270 20% 15%;
    --sidebar-border: 270 45% 82%;
    --sidebar-ring: 280 85% 55%;
  }
  .dark {
    --sidebar-background: 265 60% 16%;
