import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Plus, Edit, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Label } from "@/components/ui/label";
import type { PaperSize, InsertPaperSize } from "@shared/schema";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { isUnauthorizedError } from "@/lib/authUtils";

export default function Settings() {
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [editingPaperSize, setEditingPaperSize] = useState<PaperSize | null>(null);
  const [deletingPaperSize, setDeletingPaperSize] = useState<PaperSize | null>(null);
  const { toast } = useToast();
  const { isAdmin } = useAuth();

  const { data: paperSizes, isLoading } = useQuery<PaperSize[]>({
    queryKey: ["/api/paper-sizes"],
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/paper-sizes/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/paper-sizes"] });
      toast({
        title: "Paper size deleted",
        description: "The paper size has been removed.",
      });
      setDeletingPaperSize(null);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to delete paper size. Please try again.",
        variant: "destructive",
      });
    },
  });

  const mmToInch = (mm: number) => (mm / 25.4).toFixed(2);

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold" data-testid="text-settings-title">Settings</h1>
          <p className="text-muted-foreground mt-1">Manage system settings and configurations</p>
        </div>
      </div>

      {/* Paper Sizes Section */}
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-semibold">Barcode Paper Sizes</h2>
            <p className="text-sm text-muted-foreground">Configure available paper sizes for barcode printing</p>
          </div>
          {isAdmin && (
            <Button onClick={() => setIsAddDialogOpen(true)} data-testid="button-add-paper-size">
              <Plus className="h-4 w-4 mr-2" />
              Add Paper Size
            </Button>
          )}
        </div>

        {isLoading ? (
          <Card className="p-8">
            <div className="text-center text-muted-foreground">Loading paper sizes...</div>
          </Card>
        ) : paperSizes && paperSizes.length > 0 ? (
          <Card>
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Width (mm)</TableHead>
                  <TableHead>Height (mm)</TableHead>
                  <TableHead>Inches</TableHead>
                  <TableHead>Status</TableHead>
                  {isAdmin && <TableHead className="text-center">Actions</TableHead>}
                </TableRow>
              </TableHeader>
              <TableBody>
                {paperSizes.map((size) => (
                  <TableRow key={size.id} data-testid={`row-paper-size-${size.id}`}>
                    <TableCell className="font-medium">{size.name}</TableCell>
                    <TableCell>{size.widthMm} mm</TableCell>
                    <TableCell>{size.heightMm} mm</TableCell>
                    <TableCell className="text-muted-foreground">
                      {mmToInch(size.widthMm)}" × {mmToInch(size.heightMm)}"
                    </TableCell>
                    <TableCell>
                      {size.isActive ? (
                        <span className="text-green-600">Active</span>
                      ) : (
                        <span className="text-muted-foreground">Inactive</span>
                      )}
                    </TableCell>
                    {isAdmin && (
                      <TableCell>
                        <div className="flex items-center justify-center gap-1">
                          <Button
                            variant="ghost"
                            size="icon"
                            className="h-8 w-8 bg-blue-500/10 hover:bg-blue-500/20 text-blue-600"
                            onClick={() => setEditingPaperSize(size)}
                            data-testid={`button-edit-${size.id}`}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button
                            variant="ghost"
                            size="icon"
                            className="h-8 w-8 bg-red-500/10 hover:bg-red-500/20 text-red-600"
                            onClick={() => setDeletingPaperSize(size)}
                            data-testid={`button-delete-${size.id}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </TableCell>
                    )}
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </Card>
        ) : (
          <Card className="p-12">
            <div className="flex flex-col items-center justify-center text-center">
              <h3 className="font-semibold text-lg mb-2">No paper sizes configured</h3>
              <p className="text-muted-foreground mb-6">
                Add paper sizes to enable barcode printing
              </p>
              {isAdmin && (
                <Button onClick={() => setIsAddDialogOpen(true)}>
                  <Plus className="h-4 w-4 mr-2" />
                  Add Paper Size
                </Button>
              )}
            </div>
          </Card>
        )}
      </div>

      {/* Add/Edit Dialog */}
      {(isAddDialogOpen || editingPaperSize) && (
        <Dialog 
          open={isAddDialogOpen || !!editingPaperSize} 
          onOpenChange={() => {
            setIsAddDialogOpen(false);
            setEditingPaperSize(null);
          }}
        >
          <DialogContent>
            <PaperSizeForm
              paperSize={editingPaperSize || undefined}
              onSuccess={() => {
                setIsAddDialogOpen(false);
                setEditingPaperSize(null);
              }}
            />
          </DialogContent>
        </Dialog>
      )}

      {/* Delete Confirmation */}
      <AlertDialog open={!!deletingPaperSize} onOpenChange={() => setDeletingPaperSize(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Paper Size</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete "{deletingPaperSize?.name}"? This action cannot be undone.
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel data-testid="button-cancel-delete">Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => deletingPaperSize && deleteMutation.mutate(deletingPaperSize.id)}
              data-testid="button-confirm-delete"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  );
}

function PaperSizeForm({ paperSize, onSuccess }: { paperSize?: PaperSize; onSuccess: () => void }) {
  const { toast } = useToast();
  const [formData, setFormData] = useState<InsertPaperSize>({
    name: paperSize?.name || "",
    widthMm: paperSize?.widthMm || 0,
    heightMm: paperSize?.heightMm || 0,
    isActive: paperSize?.isActive ?? true,
  });

  const saveMutation = useMutation({
    mutationFn: async (data: InsertPaperSize) => {
      if (paperSize) {
        return await apiRequest("PATCH", `/api/paper-sizes/${paperSize.id}`, data);
      } else {
        return await apiRequest("POST", "/api/paper-sizes", data);
      }
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/paper-sizes"] });
      toast({
        title: paperSize ? "Paper size updated" : "Paper size created",
        description: paperSize ? "The paper size has been updated successfully." : "The paper size has been created successfully.",
      });
      onSuccess();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: `Failed to ${paperSize ? "update" : "create"} paper size. Please try again.`,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!formData.name || formData.widthMm <= 0 || formData.heightMm <= 0) {
      toast({
        title: "Validation Error",
        description: "Please fill in all required fields with valid values.",
        variant: "destructive",
      });
      return;
    }
    saveMutation.mutate(formData);
  };

  const mmToInch = (mm: number) => (mm / 25.4).toFixed(2);

  return (
    <form onSubmit={handleSubmit}>
      <DialogHeader>
        <DialogTitle>{paperSize ? "Edit Paper Size" : "Add Paper Size"}</DialogTitle>
      </DialogHeader>

      <div className="space-y-4 py-4">
        <div className="space-y-2">
          <Label htmlFor="name">Name *</Label>
          <Input
            id="name"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            placeholder="e.g., 38mm Label"
            required
            data-testid="input-name"
          />
        </div>

        <div className="grid grid-cols-2 gap-4">
          <div className="space-y-2">
            <Label htmlFor="widthMm">Width (mm) *</Label>
            <Input
              id="widthMm"
              type="number"
              min="1"
              value={formData.widthMm || ""}
              onChange={(e) => setFormData({ ...formData, widthMm: parseInt(e.target.value) || 0 })}
              required
              data-testid="input-width"
            />
            {formData.widthMm > 0 && (
              <p className="text-xs text-muted-foreground">{mmToInch(formData.widthMm)} inches</p>
            )}
          </div>

          <div className="space-y-2">
            <Label htmlFor="heightMm">Height (mm) *</Label>
            <Input
              id="heightMm"
              type="number"
              min="1"
              value={formData.heightMm || ""}
              onChange={(e) => setFormData({ ...formData, heightMm: parseInt(e.target.value) || 0 })}
              required
              data-testid="input-height"
            />
            {formData.heightMm > 0 && (
              <p className="text-xs text-muted-foreground">{mmToInch(formData.heightMm)} inches</p>
            )}
          </div>
        </div>

        <div className="flex items-center gap-2">
          <input
            type="checkbox"
            id="isActive"
            checked={formData.isActive}
            onChange={(e) => setFormData({ ...formData, isActive: e.target.checked })}
            className="h-4 w-4"
            data-testid="checkbox-active"
          />
          <Label htmlFor="isActive" className="font-normal cursor-pointer">
            Active (available for selection)
          </Label>
        </div>
      </div>

      <div className="flex justify-end gap-2">
        <Button type="submit" disabled={saveMutation.isPending} data-testid="button-save">
          {saveMutation.isPending ? "Saving..." : paperSize ? "Update" : "Create"}
        </Button>
      </div>
    </form>
  );
}

client/src/pages/pos.tsx.content:
import { useState, useRef, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Search, Trash2, Calendar, User, Plus, Minus, Scan } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Label } from "@/components/ui/label";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import type { Product, Customer } from "@shared/schema";

interface CartItem {
  product: Product;
  quantity: number;
}

export default function POS() {
  const [selectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [selectedCustomer, setSelectedCustomer] = useState<string>("walk-in");
  const [searchCode, setSearchCode] = useState("");
  const [cart, setCart] = useState<CartItem[]>([]);
  const [coupon, setCoupon] = useState(0);
  const [discount, setDiscount] = useState(0);
  const [shipping, setShipping] = useState(0);
  const [tax, setTax] = useState(0);
  const [scanMode, setScanMode] = useState(false);
  const searchInputRef = useRef<HTMLInputElement>(null);
  const scanTimeoutRef = useRef<NodeJS.Timeout | null>(null);

  const { data: products } = useQuery<Product[]>({
    queryKey: ["/api/products"],
  });

  const { data: customers } = useQuery<Customer[]>({
    queryKey: ["/api/customers"],
  });

  // Auto-focus search input when scan mode is active
  useEffect(() => {
    if (scanMode && searchInputRef.current) {
      searchInputRef.current.focus();
    }
  }, [scanMode]);

  // Auto-submit after scanning (simulates barcode scanner behavior)
  useEffect(() => {
    if (scanMode && searchCode.length > 0) {
      if (scanTimeoutRef.current) {
        clearTimeout(scanTimeoutRef.current);
      }

      // Auto-submit after 100ms of no input (simulates barcode scan complete)
      scanTimeoutRef.current = setTimeout(() => {
        handleSearchProduct();
        setScanMode(false);
      }, 100);
    }

    return () => {
      if (scanTimeoutRef.current) {
        clearTimeout(scanTimeoutRef.current);
      }
    };
  }, [searchCode, scanMode]);

  const handleSearchProduct = () => {
    if (!searchCode.trim()) return;

    const product = products?.find(
      p => p.sku?.toLowerCase() === searchCode.toLowerCase() || 
           p.name.toLowerCase().includes(searchCode.toLowerCase())
    );

    if (product && product.stock > 0) {
      const existingItem = cart.find(item => item.product.id === product.id);
      if (existingItem) {
        updateQuantity(product.id, 1);
      } else {
        setCart([...cart, { product, quantity: 1 }]);
      }
      setSearchCode("");
    }
  };

  const handleScanClick = () => {
    setScanMode(true);
    setSearchCode("");
    if (searchInputRef.current) {
      searchInputRef.current.focus();
    }
  };

  const updateQuantity = (productId: string, delta: number) => {
    setCart(cart.map(item => {
      if (item.product.id === productId) {
        const newQuantity = item.quantity + delta;
        if (newQuantity > item.product.stock) return item;
        return { ...item, quantity: newQuantity };
      }
      return item;
    }).filter(item => item.quantity > 0));
  };

  const removeItem = (productId: string) => {
    setCart(cart.filter(item => item.product.id !== productId));
  };

  const subtotal = cart.reduce((sum, item) => 
    sum + (parseFloat(item.product.price) * item.quantity), 0
  );

  const totalDiscount = discount + coupon;
  const grandTotal = Math.max(0, subtotal - totalDiscount + tax + shipping);
  const itemCount = cart.reduce((sum, item) => sum + item.quantity, 0);

  return (
    <div className="h-full p-6 bg-background">
      <div className="max-w-7xl mx-auto space-y-4">
        {/* Header */}
        <div className="flex items-center justify-between gap-4">
          <div className="flex items-center gap-2">
            <Calendar className="h-5 w-5 text-muted-foreground" />
            <Input
              type="date"
              value={selectedDate}
              readOnly
              className="w-48"
              data-testid="input-date"
            />
          </div>

          <div className="flex items-center gap-2 flex-1 max-w-md">
            <User className="h-5 w-5 text-muted-foreground" />
            <Select value={selectedCustomer} onValueChange={setSelectedCustomer}>
              <SelectTrigger data-testid="select-customer">
                <SelectValue placeholder="Select customer" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="walk-in">Walk-in Customer</SelectItem>
                {customers?.map((customer) => (
                  <SelectItem key={customer.id} value={customer.id}>
                    {customer.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <Badge variant="secondary" className="text-sm px-4 py-2">
            Active Lifestyle Ltd.
          </Badge>
        </div>

        {/* Product Search */}
        <Card className="p-4">
          <div className="flex items-center gap-2">
            <div className="bg-primary/10 p-2 rounded-md">
              <Search className="h-5 w-5 text-primary" />
            </div>
            <Input
              ref={searchInputRef}
              placeholder={scanMode ? "Scanning... Point scanner at barcode" : "Scan/Search product by name/code"}
              value={searchCode}
              onChange={(e) => setSearchCode(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && !scanMode && handleSearchProduct()}
              className={`flex-1 ${scanMode ? 'ring-2 ring-primary' : ''}`}
              data-testid="input-search-product"
            />
            <Button 
              variant={scanMode ? "default" : "outline"}
              onClick={handleScanClick}
              data-testid="button-scan"
            >
              <Scan className="h-4 w-4 mr-2" />
              {scanMode ? "Scanning..." : "Scan"}
            </Button>
            <Button onClick={handleSearchProduct} data-testid="button-add-product">
              Add
            </Button>
          </div>
        </Card>

        {/* Products Table */}
        <Card>
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead>Product</TableHead>
                <TableHead>Code</TableHead>
                <TableHead className="text-right">Price</TableHead>
                <TableHead className="text-center">Quantity</TableHead>
                <TableHead className="text-right">SubTotal</TableHead>
                <TableHead className="text-center">Delete</TableHead>
              </TableRow>
            </TableHeader>
            <TableBody>
              {cart.length === 0 ? (
                <TableRow>
                  <TableCell colSpan={6} className="text-center text-muted-foreground py-8">
                    No items in cart. Scan or search products to add.
                  </TableCell>
                </TableRow>
              ) : (
                cart.map((item) => (
                  <TableRow key={item.product.id}>
                    <TableCell className="font-medium">{item.product.name}</TableCell>
                    <TableCell className="text-muted-foreground">{item.product.sku || '-'}</TableCell>
                    <TableCell className="text-right tabular-nums">
                      ${parseFloat(item.product.price).toFixed(2)}
                    </TableCell>
                    <TableCell>
                      <div className="flex items-center justify-center gap-2">
                        <Button
                          variant="outline"
                          size="icon"
                          className="h-7 w-7"
                          onClick={() => updateQuantity(item.product.id, -1)}
                          data-testid={`button-decrease-${item.product.id}`}
                        >
                          <Minus className="h-3 w-3" />
                        </Button>
                        <span className="w-12 text-center font-medium tabular-nums">
                          {item.quantity}
                        </span>
                        <Button
                          variant="outline"
                          size="icon"
                          className="h-7 w-7"
                          onClick={() => updateQuantity(item.product.id, 1)}
                          data-testid={`button-increase-${item.product.id}`}
                        >
                          <Plus className="h-3 w-3" />
                        </Button>
                      </div>
                    </TableCell>
                    <TableCell className="text-right font-semibold tabular-nums">
                      ${(parseFloat(item.product.price) * item.quantity).toFixed(2)}
                    </TableCell>
                    <TableCell className="text-center">
                      <Button
                        variant="ghost"
                        size="icon"
                        className="h-8 w-8 text-destructive hover:text-destructive"
                        onClick={() => removeItem(item.product.id)}
                        data-testid={`button-delete-${item.product.id}`}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </TableCell>
                  </TableRow>
                ))
              )}
            </TableBody>
          </Table>
        </Card>

        {/* Summary Section */}
        <div className="grid grid-cols-2 gap-6">
          <div className="space-y-3">
            <div className="flex items-center justify-between">
              <Label className="text-muted-foreground">Items</Label>
              <span className="font-medium tabular-nums">{itemCount}</span>
            </div>
            <div className="flex items-center justify-between">
              <Label className="text-muted-foreground">Coupon</Label>
              <Input
                type="number"
                value={coupon}
                onChange={(e) => setCoupon(parseFloat(e.target.value) || 0)}
                className="w-32 text-right"
                step="0.01"
                data-testid="input-coupon"
              />
            </div>
            <div className="flex items-center justify-between">
              <Label className="text-muted-foreground">Tax</Label>
              <Input
                type="number"
                value={tax}
                onChange={(e) => setTax(parseFloat(e.target.value) || 0)}
                className="w-32 text-right"
                step="0.01"
